INSERT INTO `tudoonline_production`.`produtos` VALUES (10012540, 145313, 'D20-842261 - 2541319', 'T00047705G0IAX', 'Kit Completo Válvula Termostática PEUGEOT Hoggar 1.4 1.6', NULL, NULL, '2025-10-22 22:00:29', 'https://loja.umec.com.br/produtos/2541319_A.jpg', 47705, 0, 132.63, 14, NULL, NULL, NULL, NULL, NULL, '0.12', 'Válvula Termostática\nPEUGEOT - HOGGAR\n1.4 8v / 1.6 16v - Kit = Carcaça + Válvula + Sensor\n\nFunção: A válvula termostática é uma peça importantíssima do sistema de arrefecimento dos carros. Ela controla a temperatura do motor. Quando o motor está frio, ela fica fechada, fazendo com que o líquido de arrefecimento (água e aditivo) volte para o bloco do motor. A partir de uma determinada temperatura, cerca de 90 graus, ela se abre, permitindo a passagem do líquido para o radiador para que ele seja refrigerado e volte para o bloco. A manutenção preventiva a cada 30 mil km ou menos.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: 1336Z0\nCódigo do fabricante: AVTS11001T89\nCódigo interno: 2541319\n\nMarca: AUTOSTAR\n\n', '0.31', '["https://loja.umec.com.br/produtos/2541319_A.jpg"]', NULL, '0.08', NULL, '0.17', '', '', NULL, NULL, NULL, 0, 0, 1, 'MLB4268436875', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '', NULL, '[{"id":"SELLER_SKU","value_name":"T00047705G0IAX"},{"id":"EAN","value_name":"6949999894136"},{"id":"GTIN","value_name":"6949999894136"}]', NULL, NULL, NULL, 0.00, 0.00, 1.16, '6949999894136', 0, 0, NULL, NULL, NULL, 1, NULL, 20.20, NULL, NULL, NULL, NULL, NULL, NULL);
