INSERT INTO `tudoonline_production`.`produtos` VALUES (10267276, 145313, 'D20-186501 - 827959', 'T00029591H4ZeX', 'Correia Dentada FORD FIESTA 1.6 - Manutenção Segura', NULL, NULL, '2025-12-09 08:00:24', 'https://loja.umec.com.br/produtos/827959_A.jpg', 29591, 0, 145.20, 4, NULL, NULL, NULL, NULL, NULL, '0.16', 'Correia Dentada\nFORD - FIESTA\nMotor 1.6 16v Sigma - (117 Dentes) (CVA - Comando de Válvulas)\n\nFunção: A correia dentada é a responsável por manter ligado o eixo-comando de válvulas ao virabrequim do motor. Ela também faz com que ambos se sincronizem. Outra função importante executada pela correia dentada é a de fazer com que as válvulas de admissão e de escapamento se fechem e se abram no momento certo. A manutenção deve ser feita ao menos de seis em seis meses, ou então a cada 10 mil km rodados. Essa quilometragem varia de modelo para modelo.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: \nCódigo do fabricante: CT881\nCódigo interno: 827959\n\nMarca: CONTITECH\n\n\n                        ref=l9i7pRJglV', '0.19', '["https://loja.umec.com.br/produtos/827959_A.jpg"]', NULL, '0.03', NULL, '0.26', '{"BRAND":"CONTITECH","MODEL":"1","GTIN":"7898605589920","EMPTY_GTIN_REASON":"Outro motivo"}', 'MLB9190', NULL, NULL, NULL, 0, 0, 1, 'MLB6024881926', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '{"BRAND":"CONTITECH","MODEL":"1","GTIN":"7898605589920","EMPTY_GTIN_REASON":"Outro motivo"}', NULL, '[{"id":"SELLER_SKU","value_name":"T00029591H4ZeX"},{"id":"EAN","value_name":"7898605589920"},{"id":"GTIN","value_name":"7898605589920"},{"id":"BRAND","value_name":"CONTITECH"},{"id":"MODEL","value_name":"1"},{"id":"EMPTY_GTIN_REASON","value_name":"Outro motivo"}]', NULL, NULL, NULL, 0.00, 0.00, 1.16, '7898605589920', 0, 0, NULL, NULL, NULL, 1, NULL, 20.20, NULL, NULL, NULL, NULL, NULL, NULL);
