INSERT INTO `tudoonline_production`.`produtos` VALUES (10317920, 145313, 'D20-167871 - 222895', 'T00091832Hia4X', 'Bomba de Óleo Compatível DAEWOO Leganza 2.0 16V', NULL, NULL, '2026-01-03 08:00:16', 'https://loja.umec.com.br/produtos/222895_A.jpg', 91832, 0, 348.98, 0, NULL, NULL, NULL, NULL, NULL, '0.20', 'Bomba Óleo\nDAEWOO - LEGANZA\nMOTOR 2.0 16V (ROSCA DE 8mm NA BASE DE FIXAÇÃO NO CÁRTER)\n\nFunção: A bomba de óleo é o componente responsável pela lubrificação forçada do motor do veículo. Esse processo acontece quando o óleo do carter é retirado e enviado pela galeria principal de lubrificação. Quem executa todo o envio é a bomba de óleo. O óleo é enviado para várias partes do motor, que passam a ser lubrificadas constantemente.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: 90570925\nCódigo do fabricante: 25149\nCódigo interno: 222895\n\nMarca: INDISA\n\n\n                        ref=OucKaaJhy9', '1.52', '["https://loja.umec.com.br/produtos/222895_A.jpg"]', NULL, '0.10', NULL, '0.29', '{"BRAND":"INDISA","PART_NUMBER":"25149","VEHICLE_TYPE":"Carro/Caminhonete"}', 'MLB47106', NULL, NULL, NULL, 0, 0, 1, 'MLB4399027515', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '{"BRAND":"INDISA","PART_NUMBER":"25149","VEHICLE_TYPE":"Carro/Caminhonete"}', NULL, '[{"id":"SELLER_SKU","value_name":"T00091832Hia4X"},{"id":"EAN","value_name":"7898479002198"},{"id":"GTIN","value_name":"7898479002198"},{"id":"BRAND","value_name":"INDISA"},{"id":"PART_NUMBER","value_name":"25149"},{"id":"VEHICLE_TYPE","value_name":"Carro/Caminhonete"}]', NULL, NULL, NULL, 0.00, 0.00, 1.16, '7898479002198', 0, 0, NULL, NULL, NULL, 1, NULL, 23.45, NULL, NULL, NULL, NULL, NULL, NULL);
