INSERT INTO `tudoonline_production`.`produtos` VALUES (9804538, 145313, 'D20-195247 - 1125184', 'T00034969F8BIX', 'Tensor Alternador VW Quantum 76x31mm CONTITECH', NULL, NULL, '2025-09-23 20:01:08', 'http://loja.umec.com.br/produtos/1125184_B.jpg', 34969, 0, 264.99, 11, NULL, NULL, NULL, NULL, NULL, '0.10', 'Tensionador Correia Alternador\nVW - QUANTUM\n1.8/2.0 AP - (C/DH - S/ACD) (76x31mm) (Suporte com 1 Polia Lisa e 1 Estriada)\n\nFunção: Tensor correia alternador: Aplica-se na parte de fora do motor. Sua função é aplicar carga/pressão constante na correia, com a finalidade de evitar que a mesma se solte do sistema de polias, compensar eventuais folgas ou tensões excessivas que possam prejudicar o sincronismo do motor e o funcionamento dos acessórios do veículo.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: 377145299\nCódigo do fabricante: V55791\nCódigo interno: 1125184\n\nMarca: CONTITECH\n\n', '0.59', '["http://loja.umec.com.br/produtos/1125184_B.jpg","http://loja.umec.com.br/produtos/1125184_C.jpg"]', NULL, '0.09', NULL, '0.26', '', '', NULL, NULL, NULL, 0, 0, 1, 'MLB5736211306', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '', NULL, '[{"id":"SELLER_SKU","value_name":"T00034969F8BIX"},{"id":"EAN","value_name":"7898605583195"},{"id":"GTIN","value_name":"7898605583195"}]', NULL, NULL, NULL, 0.00, 0.00, 1.10, '7898605583195', 0, 0, NULL, NULL, NULL, 1, NULL, 22.45, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `tudoonline_production`.`produtos` VALUES (9804552, 145313, 'D20-507993 - 5509645', 'T00041098F8BWX', 'Filtro Ar para Cabine GM Tracker 2017 Flex', NULL, NULL, '2025-09-23 20:01:58', 'https://loja.umec.com.br/produtos/5509645_1.jpg', 41098, 0, 29.78, 29, NULL, NULL, NULL, NULL, NULL, '0.21', 'Filtro Ar Cabine\nGM - TRACKER\n(LT) - Motor 1.4 16v Turbo Flex - (Automático/Mecânico) - (Sem carvão)\n\nFunção: Os filtros do ar para cabine são produzidos com fibras carregadas eletrostaticamente. Este filtros possuem camadas protegidas de um lado por um pré-filtro para reter partículas maiores. Do lado oposto possui uma camada de proteção a danos ao filtro.A função do meio filtrante é remover a maior quantidade possível de contaminantes do sistema do ar, oferecendo o mínimo de restrição ao fluxo de ar.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: 52420930\nCódigo do fabricante: AKX35723\nCódigo interno: 5509645\n\nMarca: WEGA\n\nSobre: A Wega Motors pertence ao Grupo Argentino R. Neto S.A, fundada em 1969 por Rafael Neto. Localizada em Buenos Aires, a empresa possui três plantas industriais voltadas ao mercado de reposição para a Linha Leve e Pesada.\r\nWEGA NO BRASIL - Local Itajaí - SC, tem Diretoria, as áreas Administrativas e de TI, os Laboratórios de testes e desenvolvimento e o Centro de Distribuição e Logística. Em São Paulo, segui o Comercial, Marketing, Assistência Técnica, Treinamento, Serviços de Suporte ao Cliente.', '0.15', '["https://loja.umec.com.br/produtos/5509645_1.jpg"]', NULL, '0.04', NULL, '0.25', '', '', NULL, NULL, NULL, 0, 0, 1, 'MLB5736159308', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '', NULL, '[{"id":"SELLER_SKU","value_name":"T00041098F8BWX"},{"id":"EAN","value_name":"7798159124830"},{"id":"GTIN","value_name":"7798159124830"}]', NULL, NULL, NULL, 0.00, 0.00, 1.10, '7798159124830', 0, 0, NULL, NULL, NULL, 1, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
