INSERT INTO `tudoonline_production`.`produtos` VALUES (9936907, 145313, 'D20-1339659 - 161365', 'T00080687FH2HX', 'Coxim Traseiro Motor FIAT Palio 1.0 - Alta Performance', NULL, NULL, '2025-10-13 14:00:30', 'https://loja.umec.com.br/produtos/161365_A.jpg', 80687, 0, 169.02, 14, NULL, NULL, NULL, NULL, NULL, '0.20', 'Suporte Motor\nFIAT - PALIO\n1.0   TRASEIRO\n\nFunção: São divididos em 3 grupos: coxins do motor, coxins do câmbio e coxins do amortecedor.\r\n\r\nA função do coxim de motor/câmbio é ser o ponto de apoio do motor no chassi, fazendo a absorção da vibração do motor e evitando que seja transmitido para o veículo.\r\n\r\nO uso de coxins danificados diminuirá significativamente a vida útil do motor/câmbio do veículo.\n\nEspecificações técnicas:\n Quantidade de itens na embalagem: 1\nCódigo de referência: 51834114\nCódigo do fabricante: MB4436\nCódigo interno: 161365\n\nMarca: MOBENSANI\n\n', '0.83', '["https://loja.umec.com.br/produtos/161365_A.jpg"]', NULL, '0.10', NULL, '0.05', '{"BRAND":"Genu00e9rico","PART_NUMBER":"51834114"}', 'MLB193902', NULL, NULL, NULL, 0, 0, 1, 'MLB4249756007', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gold_pro', NULL, '{"BRAND":"Genu00e9rico","PART_NUMBER":"51834114"}', NULL, '[{"id":"SELLER_SKU","value_name":"T00080687FH2HX"},{"id":"EAN","value_name":"7898429269787"},{"id":"GTIN","value_name":"7898429269787"},{"id":"BRAND","value_name":"Genu00e9rico"},{"id":"PART_NUMBER","value_name":"51834114"}]', NULL, NULL, NULL, 0.00, 0.00, 1.10, '7898429269787', 0, 0, NULL, NULL, NULL, 1, NULL, 21.10, NULL, NULL, NULL, NULL, NULL, NULL);
